package cn.edu.cqut.weknow.controller;

import javax.servlet.http.HttpServletRequest;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.util.StringUtils;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.ResponseBody;

import com.fasterxml.jackson.databind.ObjectMapper;

import cn.edu.cqut.weknow.po.Users;
import cn.edu.cqut.weknow.service.IUserService;

@Controller
@RequestMapping("/user")
public class UserController {
	
	/*
	 *  @ResponseBody  返回json
	 *  @RequestBody   接收json
	 */
	
	@Autowired
	private IUserService userService;
	
	//把符合要求的用户对象发送给移动端(查看我的资料)
	@RequestMapping("/getUser")
	@ResponseBody    
	public Users getUser(HttpServletRequest httpServletRequest, Integer id)
	throws Exception{
		Users user =null;
		try {
				if(id==null){
					return null;
			}
			
			user = userService.find(id);
			
		} catch (Exception e) {
			System.out.println(e.getMessage());
		}
		return user;
	}
	
	
	//修改我的资料,原资料显示,发送给移动客户端
	@RequestMapping("/editUser")
	@ResponseBody
	public Users editMyInfo(HttpServletRequest httpServletRequest, Users user)
		throws Exception{
		try {
			String id = httpServletRequest.getParameter("id");
			if(!StringUtils.isEmpty(id)){
				userService.find(Integer.parseInt(id));
			}
						
		} catch (Exception e) {
			System.out.println(e.getMessage());
		}
		return user;
	}
	
	//修改提交我的资料
	@RequestMapping("/editUserSubmit")
	@ResponseBody
	public Users editSubmit(HttpServletRequest httpServletRequest, String myInfo)
		throws Exception{
		Users user = null;
		try { 
			
			//将json字符串转换成java对象
			ObjectMapper mapper = new ObjectMapper();
			user = mapper.readValue(myInfo, Users.class);
			
			//String encodedImageStr = "data:image/jpg;base64,";
			//System.out.println(encodedImageStr);

			//Base64.getDecoder().decode(encodedImageStr);
			
			//Base64Utils.decodeFromString(encodedImageStr);
			
			user.setAvatarurl("/weknow_web/userAvatar/34d486c4-3c75-4464-af14-66f6f2016339.jpg");
			userService.update(user);
		} catch (Exception e) {
			e.printStackTrace();
		}
		return user;
	}
	
}
